        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "StickyHeader": function() {
        	var getWidth = $(window).width();

            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $("#gb-sticky-header-outer").offset().top;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(CreativeTemplate.csNavIsSticky) {
                        $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                        $("#gb-sticky-header-outer").css("height", "auto");
                        CreativeTemplate.csNavIsSticky = 0;
                    }
                } else {
                    $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight());

                    if(!CreativeTemplate.csNavIsSticky) {
                        $("#gb-sticky-header-outer, #gb-page").addClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 1;
                    }

                    setTimeout(function(){
                        $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight());
                    }, 300);
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                    }
                }
            } else {
                if(CreativeTemplate.csNavIsSticky == 1) {
                    $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                    $("#gb-sticky-header-outer").css("height", "auto");

                    CreativeTemplate.csNavIsSticky = 0;
                }
            }
        },
        "Cookie": {
			"CreateCookie": function(name, value, days) {
                var expires;
                if (days) {
                    var date = new Date();
                    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                    expires = "; expires=" + date.toGMTString();
                } else {
                    expires = "";
                }
                document.cookie = encodeURIComponent(name) + "=" + encodeURIComponent(value) + expires + "; path=/";
            },
            "ReadCookie": function(name) {
                var nameEQ = encodeURIComponent(name) + "=";
                var ca = document.cookie.split(';');
                for (var i = 0; i < ca.length; i++) {
                    var c = ca[i];
                    while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                    if (c.indexOf(nameEQ) === 0) return decodeURIComponent(c.substring(nameEQ.length, c.length));
                }
                return null;
            },
            "EraseCookie": function(name) {
                CreativeTemplate.Cookie.CreateCookie(name, "", -1);
            }
        },
        "CsCustomADA": function() {
        	$('#gb-translate-options .mystart-label').keydown(function(e){
            	if(e.which == 9 && e.shiftKey){
                	e.preventDefault();
                    $('#ada-panel-trigger').focus();
                }
            });

        	$("#gb-custom-ada-panel a, #gb-custom-ada-panel button").attr('tabindex', -1);
            //OPEN CUSTOMIZATION OPTIONS
            $("#ada-panel-trigger").off('click').click(function(e) {
                e.preventDefault();

                if( $(this).hasClass("open") ){
                	$(this).attr("aria-expanded", "false");
                    $("#gb-custom-ada-panel").attr("aria-hidden", "true");
                    $("#gb-custom-ada-panel a, #gb-custom-ada-panel button").attr('tabindex', -1);
                }else{
                	$(this).attr("aria-expanded", "true");
                    $("#gb-custom-ada-panel").attr("aria-hidden", "false");
                    $("#gb-custom-ada-panel a, #gb-custom-ada-panel button").attr('tabindex', 0);
                }

                $(this).toggleClass("open");
                $("#gb-custom-ada-panel").slideToggle(300);
                if( $(this).hasClass("open") ){
                	$('#gb-translate-options button').focus();
                }


                setTimeout(function(){
                    if(CreativeTemplate.csNavIsSticky){
                        $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight());
                    }
                }, 350);
            });

            //CONTRAST OPTIONS
            $("#custom-contrast .contrast").click(function(e){
                e.preventDefault();
                //Clear the data
                CreativeTemplate.Cookie.EraseCookie("creativeTemplateAdaContrast");
                $("body").removeClass("contrast-black-yellow contrast-white-black contrast-black-white");

                //Set the data
                if(!$(this).hasClass("active")){
                    $("#custom-contrast .contrast.active").removeClass("active");
                    CreativeTemplate.Cookie.CreateCookie("creativeTemplateAdaContrast", $(this).attr('data-contrast'));
                    $("body").addClass($(this).attr('data-contrast'));
                    $(this).addClass("active");
                }else{
                	$(this).removeClass("active");
                }
            });

            //On page load check if we have a cookie
            if(CreativeTemplate.Cookie.ReadCookie("creativeTemplateAdaContrast") != null){
                var contrastClass = CreativeTemplate.Cookie.ReadCookie("creativeTemplateAdaContrast");
                //class the body
                $("body").addClass(contrastClass);
                //class the active contrast button
                $("#custom-contrast .contrast[data-contrast='" + contrastClass + "']").addClass('active');
            }


            //TEXT OPTIONS
            $("#custom-text-size .text").click(function(e) {
                e.preventDefault();
                //Clear the data
                CreativeTemplate.Cookie.EraseCookie("creativeTemplateAdaText");
                $("body").removeClass("text-size-medium text-size-large");
				//Set the data
                if(!$(this).hasClass("active") && !$(this).hasClass('default')){
                    $("#custom-text-size .text.active").removeClass("active");
                    CreativeTemplate.Cookie.CreateCookie("creativeTemplateAdaText", $(this).attr('data-text-size'));
                    $("body").addClass($(this).attr('data-text-size'));
                    $(this).addClass("active");
                }else{
                	$(this).removeClass("active");
                }
            });

            //On page load check if we have a cookie
            if(CreativeTemplate.Cookie.ReadCookie("creativeTemplateAdaText") != null){
                var textSize = CreativeTemplate.Cookie.ReadCookie("creativeTemplateAdaText");
                //class the body
                $("body").addClass(textSize);
                //class the active text size button
                $("#custom-text-size .text[data-text-size='" + textSize + "']").addClass('active');
            }
        },


